#include <iostream>
#include <vector>

std::vector<std::vector<float>> reshape_matrix(const std::vector<std::vector<float>>& matrix, int new_rows, int new_cols);

void print_matrix(const std::vector<std::vector<float>>& m) {
    std::cout << "[";
    for (int i = 0; i < m.size(); i++) {
        std::cout << "[";
        for (int j = 0; j < m[i].size(); j++) {
            std::cout << (int)m[i][j];
            if (j < m[i].size() - 1) std::cout << ", ";
        }
        std::cout << "]";
        if (i < m.size() - 1) std::cout << ", ";
    }
    std::cout << "]" << std::endl;
}

int main() {
    std::vector<std::vector<float>> matrix = {{1,2,3,4},{5,6,7,8}};
    auto result = reshape_matrix(matrix, 4, 2);
    print_matrix(result);
    return 0;
}

