#include <iostream>
#include <vector>
#include <string>

std::vector<float> calculate_matrix_mean(const std::vector<std::vector<float>>& matrix, const std::string& mode);

int main() {
    std::vector<std::vector<float>> matrix = {{1, 2, 3}, {4, 5, 6}, {7, 8, 9}};
    auto result = calculate_matrix_mean(matrix, "column");
    std::cout << "[" << result[0] << ", " << result[1] << ", " << result[2] << "]" << std::endl;
    return 0;
}

