#include <iostream>
#include <vector>

std::vector<std::vector<float>> scalar_multiply(const std::vector<std::vector<float>>& matrix, float scalar);

void print_matrix(const std::vector<std::vector<float>>& m) {
    std::cout << "[";
    for (int i = 0; i < m.size(); i++) {
        std::cout << "[";
        for (int j = 0; j < m[i].size(); j++) {
            std::cout << (int)m[i][j];
            if (j < m[i].size() - 1) std::cout << ", ";
        }
        std::cout << "]";
        if (i < m.size() - 1) std::cout << ", ";
    }
    std::cout << "]" << std::endl;
}

int main() {
    std::vector<std::vector<float>> matrix = {{1,2},{3,4}};
    auto result = scalar_multiply(matrix, 2);
    print_matrix(result);
    return 0;
}

