#include <iostream>
#include <vector>

std::vector<float> calculate_eigenvalues(const std::vector<std::vector<float>>& matrix);

int main() {
    std::vector<std::vector<float>> matrix = {{2, 1}, {1, 2}};
    auto result = calculate_eigenvalues(matrix);
    std::cout << "[" << result[0] << ", " << result[1] << "]" << std::endl;
    return 0;
}

