#include <iostream>
#include <vector>

std::vector<std::vector<float>> inverse_2x2(const std::vector<std::vector<float>>& matrix);

int main() {
    std::vector<std::vector<float>> matrix = {{4, 7}, {2, 6}};
    auto result = inverse_2x2(matrix);
    std::cout << "[[" << result[0][0] << ", " << result[0][1] << "], [" << result[1][0] << ", " << result[1][1] << "]]" << std::endl;
    return 0;
}

