// ============================================
// Result Bar
// ============================================
function ResultBar({ value, maxValue, index, highlighted }) {
  const percentage = Math.min(Math.abs(value) / maxValue * 100, 100);
  const isNegative = value < 0;

  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
      <div
        style={{
          width: '36px',
          height: '36px',
          borderRadius: '8px',
          background: highlighted ? theme.colors.primary : theme.colors.surfaceAlt,
          border: `1px solid ${highlighted ? theme.colors.primary : theme.colors.border}`,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          fontFamily: theme.font,
          fontSize: '14px',
          fontWeight: '600',
          color: highlighted ? theme.colors.secondary : theme.colors.textSecondary,
        }}
      >
        {index + 1}
      </div>
      <div style={{ flex: 1 }}>
        <div
          style={{
            height: '24px',
            background: theme.colors.surfaceAlt,
            borderRadius: '6px',
            overflow: 'hidden',
            position: 'relative',
          }}
        >
          <div
            style={{
              position: 'absolute',
              left: isNegative ? 'auto' : '0',
              right: isNegative ? '50%' : 'auto',
              width: `${percentage / 2}%`,
              height: '100%',
              background: highlighted 
                ? `linear-gradient(90deg, ${theme.colors.primary}, ${theme.colors.primaryHover})`
                : theme.colors.borderLight,
              borderRadius: '6px',
              transition: 'width 0.3s ease',
            }}
          />
          <div
            style={{
              position: 'absolute',
              left: '50%',
