        }}
      >
        {value.toFixed(1)}
      </div>
    </div>
  );
}

// ============================================
// Calculation Step
// ============================================
function CalculationStep({ row, vector, rowIndex, isActive }) {
  const products = row.map((val, i) => val * vector[i]);
  const sum = products.reduce((a, b) => a + b, 0);

  if (!isActive) return null;

  return (
    <div
      style={{
        background: theme.colors.surfaceAlt,
        border: `1px solid ${theme.colors.primary}40`,
        borderRadius: '8px',
        padding: '16px',
        marginTop: '16px',
      }}
    >
      <div style={{ 
        fontSize: '12px', 
        color: theme.colors.textMuted, 
        marginBottom: '12px',
        fontFamily: theme.font,
        textTransform: 'uppercase',
        letterSpacing: '0.5px',
      }}>
        Row {rowIndex + 1} Calculation
      </div>

      <div style={{ 
        display: 'flex', 
        flexWrap: 'wrap',
        alignItems: 'center', 
        gap: '8px',
        fontFamily: theme.font,
        fontSize: '14px',
      }}>
        {row.map((val, i) => (
          <React.Fragment key={i}>
            {i > 0 && <span style={{ color: theme.colors.textMuted }}>+</span>}
            <span style={{ color: theme.colors.textSecondary }}>
              (<span style={{ color: theme.colors.primary }}>{val}</span>
              <span style={{ color: theme.colors.textMuted }}> × </span>
              <span style={{ color: theme.colors.success }}>{vector[i]}</span>)
            </span>
          </React.Fragment>
        ))}
        <span style={{ color: theme.colors.textMuted }}>=</span>
        {products.map((p, i) => (
          <React.Fragment key={i}>
            {i > 0 && <span style={{ color: theme.colors.textMuted }}>+</span>}
            <span style={{ color: theme.colors.textSecondary }}>{p}</span>
          </React.Fragment>
        ))}
        <span style={{ color: theme.colors.textMuted }}>=</span>
        <span style={{ 
          color: theme.colors.text, 
          fontWeight: '700',
          background: theme.colors.primary + '20',
          padding: '2px 8px',
          borderRadius: '4px',
        }}>
          {sum}
        </span>
      </div>
    </div>
  );
}

