export default function MatrixVectorDot() {
  const [matrix, setMatrix] = useState([
    [1, 2, 3],
    [4, 5, 6],
    [7, 8, 9],
  ]);
  const [vector, setVector] = useState([1, 2, 3]);
  const [activeRow, setActiveRow] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false);
  const [showSteps, setShowSteps] = useState(false);

  const rows = matrix.length;
  const cols = matrix[0].length;
  const vectorLen = vector.length;
  const isValid = cols === vectorLen;

  const result = useMemo(() => {
    if (!isValid) return null;
    return matrix.map(row => 
      row.reduce((sum, val, i) => sum + val * vector[i], 0)
    );
  }, [matrix, vector, isValid]);

  const maxResult = result ? Math.max(...result.map(Math.abs), 1) : 1;

  const updateMatrix = (rowIdx, colIdx, value) => {
    const newMatrix = matrix.map((row, r) =>
      row.map((cell, c) => (r === rowIdx && c === colIdx ? value : cell))
    );
    setMatrix(newMatrix);
  };

  const updateVector = (idx, value) => {
    const newVector = vector.map((v, i) => (i === idx ? value : v));
    setVector(newVector);
  };

  const addRow = () => setMatrix([...matrix, Array(cols).fill(0)]);
  const removeRow = () => rows > 1 && setMatrix(matrix.slice(0, -1));
  const addCol = () => {
    setMatrix(matrix.map(row => [...row, 0]));
    setVector([...vector, 0]);
  };
  const removeCol = () => {
    if (cols > 1) {
      setMatrix(matrix.map(row => row.slice(0, -1)));
      setVector(vector.slice(0, -1));
    }
  };

  const animate = async () => {
    setIsAnimating(true);
    setShowSteps(true);
    for (let i = 0; i < rows; i++) {
      setActiveRow(i);
      await new Promise(r => setTimeout(r, 800));
    }
    setActiveRow(null);
    setIsAnimating(false);
  };

  const reset = () => {
    setMatrix([[1, 2, 3], [4, 5, 6], [7, 8, 9]]);
    setVector([1, 2, 3]);
    setActiveRow(null);
    setShowSteps(false);
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        background: theme.colors.background,
        color: theme.colors.text,
        fontFamily: theme.font,
        padding: '32px',
      }}
    >
      <div style={{ maxWidth: '720px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{ marginBottom: '32px' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '8px' }}>
            <Icons.Grid3X3 size={24} color={theme.colors.primary} />
            <h1 style={{ fontSize: '24px', fontWeight: '600', margin: 0 }}>
              Matrix-Vector Dot Product
            </h1>
          </div>
          <p style={{ color: theme.colors.textSecondary, fontSize: '14px', margin: 0 }}>
            Each row of the matrix is dotted with the vector to produce a result
          </p>
        </div>

        {/* Dimension Controls */}
        <Card>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            marginBottom: '24px',
          }}>
            <div style={{ fontSize: '12px', color: theme.colors.textMuted, textTransform: 'uppercase', letterSpacing: '0.5px' }}>
              Dimensions
            </div>
            <div style={{ display: 'flex', gap: '8px' }}>
              <div style={{ 
                padding: '6px 12px', 
                background: theme.colors.surfaceAlt, 
                borderRadius: '6px',
                fontSize: '13px',
                color: theme.colors.textSecondary,
              }}>
                Matrix: {rows} × {cols}
              </div>
              <div style={{ 
                padding: '6px 12px', 
                background: theme.colors.surfaceAlt, 
                borderRadius: '6px',
                fontSize: '13px',
                color: theme.colors.textSecondary,
              }}>
                Vector: {vectorLen}
              </div>
            </div>
          </div>

          <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
            {[
              { label: '+ Row', action: addRow },
              { label: '- Row', action: removeRow },
              { label: '+ Col', action: addCol },
              { label: '- Col', action: removeCol },
            ].map(({ label, action }) => (
              <button
                key={label}
                onClick={action}
                style={{
                  padding: '8px 16px',
                  background: theme.colors.surfaceAlt,
                  border: `1px solid ${theme.colors.border}`,
                  borderRadius: '6px',
                  color: theme.colors.textSecondary,
                  fontFamily: theme.font,
                  fontSize: '13px',
                  cursor: 'pointer',
                  transition: 'all 0.2s',
                }}
                onMouseEnter={(e) => {
                  e.target.style.background = theme.colors.borderLight;
                  e.target.style.color = theme.colors.text;
                }}
                onMouseLeave={(e) => {
                  e.target.style.background = theme.colors.surfaceAlt;
                  e.target.style.color = theme.colors.textSecondary;
                }}
              >
                {label}
              </button>
            ))}
          </div>
        </Card>

        {/* Main Visualization */}
        <Card style={{ marginTop: '16px' }}>
          {/* Validity Status */}
          <div
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '8px',
              padding: '12px 16px',
              background: isValid ? `${theme.colors.success}10` : `${theme.colors.error}10`,
              border: `1px solid ${isValid ? theme.colors.success : theme.colors.error}30`,
              borderRadius: '8px',
              marginBottom: '24px',
            }}
          >
            {isValid ? (
              <Icons.Check size={16} color={theme.colors.success} />
            ) : (
              <Icons.AlertCircle size={16} color={theme.colors.error} />
            )}
            <span style={{ 
              fontSize: '13px', 
              color: isValid ? theme.colors.success : theme.colors.error 
            }}>
              {isValid 
                ? `Valid: ${cols} columns = ${vectorLen} vector length`
                : `Invalid: ${cols} columns ≠ ${vectorLen} vector length`
              }
            </span>
          </div>

          {/* Matrix × Vector = Result */}
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: '24px',
            flexWrap: 'wrap',
            justifyContent: 'center',
          }}>
            {/* Matrix */}
            <div>
              <div style={{ 
                fontSize: '11px', 
                color: theme.colors.textMuted, 
                marginBottom: '8px',
                textTransform: 'uppercase',
                letterSpacing: '0.5px',
              }}>
                Matrix A
              </div>
              <div
                style={{
                  display: 'inline-flex',
                  flexDirection: 'column',
                  gap: '8px',
                  padding: '12px',
                  background: theme.colors.surfaceAlt,
                  borderRadius: '8px',
                  border: `1px solid ${theme.colors.border}`,
                }}
              >
                {matrix.map((row, rowIdx) => (
                  <div key={rowIdx} style={{ display: 'flex', gap: '8px' }}>
                    {row.map((cell, colIdx) => (
                      <MatrixCell
                        key={colIdx}
                        value={cell}
                        onChange={(v) => updateMatrix(rowIdx, colIdx, v)}
                        highlighted={activeRow === rowIdx}
                        highlightColor={theme.colors.primary}
                      />
                    ))}
                  </div>
                ))}
              </div>
            </div>

            {/* Multiply Icon */}
            <Icons.X size={20} color={theme.colors.textMuted} />

            {/* Vector */}
            <div>
              <div style={{ 
                fontSize: '11px', 
                color: theme.colors.textMuted, 
                marginBottom: '8px',
                textTransform: 'uppercase',
                letterSpacing: '0.5px',
              }}>
                Vector b
              </div>
              <div
                style={{
                  display: 'inline-flex',
                  flexDirection: 'column',
                  gap: '8px',
                  padding: '12px',
                  background: theme.colors.surfaceAlt,
                  borderRadius: '8px',
                  border: `1px solid ${theme.colors.border}`,
                }}
              >
                {vector.map((val, idx) => (
                  <VectorCell
                    key={idx}
                    value={val}
                    onChange={(v) => updateVector(idx, v)}
                    highlighted={activeRow !== null}
                    highlightColor={theme.colors.success}
                  />
                ))}
              </div>
            </div>

            {/* Equals */}
            <Icons.ArrowRight size={20} color={theme.colors.textMuted} />

            {/* Result */}
            <div>
              <div style={{ 
                fontSize: '11px', 
                color: theme.colors.textMuted, 
                marginBottom: '8px',
                textTransform: 'uppercase',
                letterSpacing: '0.5px',
              }}>
                Result
              </div>
              <div
                style={{
                  display: 'inline-flex',
                  flexDirection: 'column',
                  gap: '8px',
                  padding: '12px',
                  background: theme.colors.surfaceAlt,
                  borderRadius: '8px',
                  border: `1px solid ${theme.colors.border}`,
                  minWidth: '60px',
                }}
              >
                {isValid && result ? (
                  result.map((val, idx) => (
                    <div
                      key={idx}
                      style={{
                        height: '48px',
                        padding: '0 12px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        background: activeRow === idx ? `${theme.colors.primary}20` : 'transparent',
                        border: `2px solid ${activeRow === idx ? theme.colors.primary : 'transparent'}`,
                        borderRadius: '8px',
                        fontWeight: '700',
                        fontSize: '16px',
                        color: activeRow === idx ? theme.colors.primary : theme.colors.text,
                        transition: 'all 0.2s',
                      }}
                    >
                      {val}
                    </div>
                  ))
                ) : (
                  <div
                    style={{
                      height: '48px',
                      padding: '0 12px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: theme.colors.error,
                      fontWeight: '700',
                      fontSize: '16px',
                    }}
                  >
                    -1
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Calculation Steps */}
          {showSteps && isValid && (
            <div style={{ marginTop: '24px' }}>
              {matrix.map((row, idx) => (
                <CalculationStep
                  key={idx}
                  row={row}
                  vector={vector}
                  rowIndex={idx}
                  isActive={activeRow === null || activeRow === idx}
                />
              ))}
            </div>
          )}

          {/* Controls - moved here, closer to animation */}
          <div style={{ display: 'flex', gap: '12px', marginTop: '24px' }}>
            <button
              onClick={animate}
              disabled={!isValid || isAnimating}
              style={{
                flex: 1,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px',
                padding: '14px 24px',
                background: isValid ? theme.colors.primary : theme.colors.surfaceAlt,
                border: 'none',
                borderRadius: '8px',
                color: isValid ? theme.colors.secondary : theme.colors.textMuted,
                fontFamily: theme.font,
                fontSize: '14px',
                fontWeight: '600',
                cursor: isValid ? 'pointer' : 'not-allowed',
                opacity: isAnimating ? 0.7 : 1,
                transition: 'all 0.2s',
              }}
            >
              <Icons.Play size={16} />
              {isAnimating ? 'Animating...' : 'Animate Steps'}
            </button>

            <button
              onClick={() => setShowSteps(!showSteps)}
              disabled={!isValid}
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px',
                padding: '14px 24px',
                background: theme.colors.surfaceAlt,
                border: `1px solid ${theme.colors.border}`,
                borderRadius: '8px',
                color: theme.colors.textSecondary,
                fontFamily: theme.font,
                fontSize: '14px',
                fontWeight: '500',
                cursor: isValid ? 'pointer' : 'not-allowed',
