            <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
              {result.map((val, idx) => (
                <ResultBar
                  key={idx}
                  value={val}
                  maxValue={maxResult}
                  index={idx}
                  highlighted={activeRow === idx || activeRow === null}
                />
              ))}
            </div>
          </Card>
        )}

        {/* Code Output */}
        <Card style={{ marginTop: '16px' }}>
          <div style={{ 
            fontSize: '11px', 
            color: theme.colors.textMuted, 
            marginBottom: '12px',
            textTransform: 'uppercase',
            letterSpacing: '0.5px',
          }}>
            Python Output
          </div>
          <pre
            style={{
              margin: 0,
              padding: '16px',
              background: theme.colors.background,
              borderRadius: '8px',
              fontSize: '13px',
              overflow: 'auto',
            }}
          >
            <code>
              <span style={{ color: theme.colors.textMuted }}># Input</span>
              {'\n'}
              <span style={{ color: theme.colors.primary }}>matrix_dot_vector</span>
              <span style={{ color: theme.colors.text }}>(</span>
              {'\n'}
              <span style={{ color: theme.colors.textSecondary }}>    {JSON.stringify(matrix)},</span>
              {'\n'}
              <span style={{ color: theme.colors.success }}>    {JSON.stringify(vector)}</span>
              {'\n'}
              <span style={{ color: theme.colors.text }}>)</span>
              {'\n\n'}
              <span style={{ color: theme.colors.textMuted }}># Output</span>
              {'\n'}
              <span style={{ color: theme.colors.text }}>
                {isValid ? JSON.stringify(result) : '-1'}
              </span>
            </code>
          </pre>
        </Card>
      </div>
    </div>
  );
}
