export default function TransposeMatrix() {
  const [matrix, setMatrix] = useState([
    [1, 2, 3],
    [4, 5, 6],
  ]);
  const [isAnimating, setIsAnimating] = useState(false);
  const [highlightedRow, setHighlightedRow] = useState(null);
  const [showMapping, setShowMapping] = useState(false);

  const rows = matrix.length;
  const cols = matrix[0].length;

  const transposed = useMemo(() => {
    return matrix[0].map((_, colIdx) => 
      matrix.map(row => row[colIdx])
    );
  }, [matrix]);

  const updateMatrix = (rowIdx, colIdx, value) => {
    const newMatrix = matrix.map((row, r) =>
      row.map((cell, c) => (r === rowIdx && c === colIdx ? value : cell))
    );
    setMatrix(newMatrix);
  };

  const addRow = () => {
    if (rows < 5) {
      setMatrix([...matrix, Array(cols).fill(0)]);
    }
  };

  const removeRow = () => {
    if (rows > 1) {
      setMatrix(matrix.slice(0, -1));
    }
  };

  const addCol = () => {
    if (cols < 5) {
      setMatrix(matrix.map(row => [...row, 0]));
    }
  };

  const removeCol = () => {
    if (cols > 1) {
      setMatrix(matrix.map(row => row.slice(0, -1)));
    }
  };

  const animate = async () => {
    setIsAnimating(true);
    setShowMapping(true);

    for (let i = 0; i < rows; i++) {
      setHighlightedRow(i);
      await new Promise(r => setTimeout(r, 800));
    }

    setHighlightedRow(null);
    setIsAnimating(false);
  };

  const reset = () => {
    setMatrix([[1, 2, 3], [4, 5, 6]]);
    setHighlightedRow(null);
    setShowMapping(false);
  };

  const randomize = () => {
    const newMatrix = matrix.map(row =>
      row.map(() => Math.floor(Math.random() * 19) - 9)
    );
    setMatrix(newMatrix);
  };

  const activeMapping = highlightedRow !== null ? { i: highlightedRow, j: 0 } : null;

  return (
    <div
      style={{
        minHeight: '100vh',
        background: theme.colors.background,
        color: theme.colors.text,
        fontFamily: theme.font,
        padding: '32px',
      }}
    >
      <div style={{ maxWidth: '800px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{ marginBottom: '32px' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '8px' }}>
            <Icons.FlipHorizontal size={24} color={theme.colors.primary} />
            <h1 style={{ fontSize: '24px', fontWeight: '600', margin: 0 }}>
              Matrix Transpose
            </h1>
          </div>
          <p style={{ color: theme.colors.textSecondary, fontSize: '14px', margin: 0 }}>
            Rows become columns, columns become rows: A[i][j] → A<sup>T</sup>[j][i]
          </p>
        </div>

        {/* Dimension Controls */}
        <Card>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            marginBottom: '20px',
          }}>
            <div style={{ fontSize: '12px', color: theme.colors.textMuted, textTransform: 'uppercase', letterSpacing: '0.5px' }}>
              Dimensions
            </div>
            <div style={{ display: 'flex', gap: '8px' }}>
              <div style={{ 
                padding: '6px 12px', 
                background: theme.colors.surfaceAlt, 
                borderRadius: '6px',
                fontSize: '13px',
                color: theme.colors.textSecondary,
              }}>
                Input: {rows} × {cols}
              </div>
              <div style={{ 
                padding: '6px 12px', 
                background: theme.colors.primary + '20', 
                borderRadius: '6px',
                fontSize: '13px',
                color: theme.colors.primary,
              }}>
                Output: {cols} × {rows}
              </div>
            </div>
          </div>

          <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
            {[
              { label: '+ Row', action: addRow, disabled: rows >= 5 },
              { label: '- Row', action: removeRow, disabled: rows <= 1 },
              { label: '+ Col', action: addCol, disabled: cols >= 5 },
              { label: '- Col', action: removeCol, disabled: cols <= 1 },
            ].map(({ label, action, disabled }) => (
              <button
                key={label}
                onClick={action}
                disabled={disabled}
                style={{
                  padding: '8px 16px',
                  background: theme.colors.surfaceAlt,
                  border: `1px solid ${theme.colors.border}`,
                  borderRadius: '6px',
                  color: disabled ? theme.colors.textMuted : theme.colors.textSecondary,
                  fontFamily: theme.font,
                  fontSize: '13px',
                  cursor: disabled ? 'not-allowed' : 'pointer',
                  opacity: disabled ? 0.5 : 1,
                  transition: 'all 0.2s',
                }}
              >
                {label}
              </button>
            ))}

            <button
              onClick={randomize}
              style={{
                padding: '8px 16px',
