import React, { useState, useEffect, useRef, useMemo } from 'react';

const theme = {
  colors: {
    primary: '#8b5cf6',
    background: '#0a0a0a',
    surface: '#111111',
    surfaceAlt: '#1a1a1a',
    border: '#2a2a2a',
    text: '#fafafa',
    textSecondary: '#a1a1aa',
    textMuted: '#52525b',
    positive: '#10b981',
    negative: '#f43f5e',
    blue: '#38bdf8',
    yellow: '#fbbf24',
    pink: '#f472b6',
    green: '#a3e635',
    orange: '#fb923c',
  },
  font: "'JetBrains Mono', monospace",
};

const sigmoid = (z) => 1 / (1 + Math.exp(-Math.max(-500, Math.min(500, z))));

export default function App() {
  const [step, setStep] = useState(0);
  const [selectedExample, setSelectedExample] = useState(0);
  const [weights, setWeights] = useState([0.8, -0.5]);
  const [bias, setBias] = useState(0.1);

  const examples = [
    { features: [1.0, 0.5], label: 1 },
    { features: [-0.5, 1.2], label: 0 },
    { features: [0.3, -0.8], label: 1 },
  ];

  const numSteps = 4;

  const results = useMemo(() => {
    return examples.map((ex) => {
      const z = ex.features[0] * weights[0] + ex.features[1] * weights[1] + bias;
      const prob = sigmoid(z);
      const error = Math.pow(prob - ex.label, 2);
      return { ...ex, z, prob, error };
    });
  }, [weights, bias]);

  const curr = results[selectedExample];
  const mse = results.reduce((s, r) => s + r.error, 0) / results.length;

  const stepInfo = [
    { title: 'Explore', desc: 'Drag sliders to adjust weights and bias. Watch the neuron compute in real-time.', color: theme.colors.primary },
    { title: 'Weighted Sum', desc: 'Multiply each input by its weight, then add bias to get z.', color: theme.colors.blue },
    { title: 'Activation', desc: 'Sigmoid squashes z into a probability between 0 and 1.', color: theme.colors.yellow },
    { title: 'Loss', desc: 'Compare prediction to true label. MSE measures overall error.', color: theme.colors.green },
  ];

  // Network visualization component
  const NetworkDiagram = () => {
    const feat = curr.features;
    const showWeighted = step >= 1;
    const showActivation = step >= 2;
    const showOutput = step >= 2;

    return (
      <svg viewBox="0 0 400 200" style={{ width: '100%', height: '180px' }}>
        {/* Background */}
        <rect x="0" y="0" width="400" height="200" fill={theme.colors.surface} rx="8" />

        {/* Connection lines */}
        {/* x1 to neuron */}
        <line x1="70" y1="60" x2="175" y2="100" 
          stroke={showWeighted ? theme.colors.blue : theme.colors.border} 
          strokeWidth={showWeighted ? 3 : 2} 
          strokeDasharray={showWeighted ? "none" : "4,4"} />
        {/* x2 to neuron */}
        <line x1="70" y1="140" x2="175" y2="100" 
          stroke={showWeighted ? theme.colors.pink : theme.colors.border} 
          strokeWidth={showWeighted ? 3 : 2}
          strokeDasharray={showWeighted ? "none" : "4,4"} />
        {/* bias to neuron */}
        <line x1="140" y1="30" x2="175" y2="85" 
          stroke={showWeighted ? theme.colors.orange : theme.colors.border} 
          strokeWidth={showWeighted ? 2 : 1}
          strokeDasharray={showWeighted ? "none" : "4,4"} />
        {/* neuron to output */}
        <line x1="225" y1="100" x2="300" y2="100" 
          stroke={showOutput ? theme.colors.yellow : theme.colors.border} 
          strokeWidth={showOutput ? 3 : 2}
          strokeDasharray={showOutput ? "none" : "4,4"} />

        {/* Weight labels on connections */}
        {showWeighted && (
          <>
            <rect x="95" y="52" width="45" height="18" rx="4" fill={theme.colors.blue + '30'} />
            <text x="117" y="65" textAnchor="middle" fill={theme.colors.blue} fontSize="10" fontFamily="JetBrains Mono">
              w1={weights[0].toFixed(1)}
            </text>

            <rect x="95" y="125" width="45" height="18" rx="4" fill={theme.colors.pink + '30'} />
            <text x="117" y="138" textAnchor="middle" fill={theme.colors.pink} fontSize="10" fontFamily="JetBrains Mono">
              w2={weights[1].toFixed(1)}
            </text>
          </>
        )}

        {/* Input nodes */}
        <circle cx="50" cy="60" r="22" fill={theme.colors.surfaceAlt} stroke={theme.colors.blue} strokeWidth="2" />
        <text x="50" y="56" textAnchor="middle" fill={theme.colors.textMuted} fontSize="9" fontFamily="JetBrains Mono">x1</text>
        <text x="50" y="68" textAnchor="middle" fill={theme.colors.blue} fontSize="12" fontWeight="bold" fontFamily="JetBrains Mono">
          {feat[0].toFixed(1)}
        </text>

        <circle cx="50" cy="140" r="22" fill={theme.colors.surfaceAlt} stroke={theme.colors.pink} strokeWidth="2" />
        <text x="50" y="136" textAnchor="middle" fill={theme.colors.textMuted} fontSize="9" fontFamily="JetBrains Mono">x2</text>
        <text x="50" y="148" textAnchor="middle" fill={theme.colors.pink} fontSize="12" fontWeight="bold" fontFamily="JetBrains Mono">
          {feat[1].toFixed(1)}
        </text>

        {/* Bias node */}
        <circle cx="140" cy="22" r="14" fill={theme.colors.surfaceAlt} stroke={theme.colors.orange} strokeWidth="2" />
        <text x="140" y="18" textAnchor="middle" fill={theme.colors.textMuted} fontSize="8" fontFamily="JetBrains Mono">b</text>
        <text x="140" y="28" textAnchor="middle" fill={theme.colors.orange} fontSize="10" fontWeight="bold" fontFamily="JetBrains Mono">
          {bias.toFixed(1)}
        </text>

        {/* Neuron body */}
        <circle cx="200" cy="100" r="30" fill={theme.colors.surfaceAlt} 
          stroke={showActivation ? theme.colors.yellow : theme.colors.border} strokeWidth="3" />

        {/* Neuron internal - sum symbol or sigmoid */}
        {!showActivation ? (
          <text x="200" y="105" textAnchor="middle" fill={theme.colors.textMuted} fontSize="20" fontFamily="JetBrains Mono">
            S
          </text>
        ) : (
          <>
            <text x="200" y="96" textAnchor="middle" fill={theme.colors.textMuted} fontSize="9" fontFamily="JetBrains Mono">
              sig(z)
            </text>
            <text x="200" y="112" textAnchor="middle" fill={theme.colors.yellow} fontSize="11" fontWeight="bold" fontFamily="JetBrains Mono">
              {curr.z.toFixed(2)}
            </text>
          </>
        )}

        {/* z value below neuron */}
        {showWeighted && !showActivation && (
          <>
            <rect x="170" y="140" width="60" height="20" rx="4" fill={theme.colors.blue + '20'} stroke={theme.colors.blue} />
            <text x="200" y="154" textAnchor="middle" fill={theme.colors.blue} fontSize="11" fontWeight="bold" fontFamily="JetBrains Mono">
              z = {curr.z.toFixed(2)}
            </text>
          </>
        )}

        {/* Output node */}
        <circle cx="320" cy="100" r="26" 
          fill={showOutput ? (curr.prob > 0.5 ? theme.colors.positive + '30' : theme.colors.negative + '30') : theme.colors.surfaceAlt} 
          stroke={showOutput ? (curr.prob > 0.5 ? theme.colors.positive : theme.colors.negative) : theme.colors.border} 
          strokeWidth="3" />

        {showOutput ? (
          <>
            <text x="320" y="96" textAnchor="middle" fill={theme.colors.textMuted} fontSize="8" fontFamily="JetBrains Mono">
              prob
            </text>
            <text x="320" y="110" textAnchor="middle" 
              fill={curr.prob > 0.5 ? theme.colors.positive : theme.colors.negative} 
              fontSize="13" fontWeight="bold" fontFamily="JetBrains Mono">
              {curr.prob.toFixed(3)}
            </text>
          </>
        ) : (
          <text x="320" y="104" textAnchor="middle" fill={theme.colors.textMuted} fontSize="10" fontFamily="JetBrains Mono">
            output
          </text>
        )}

        {/* True label indicator */}
        {step >= 3 && (
          <>
            <rect x="355" y="85" width="40" height="30" rx="6" 
              fill={curr.label === 1 ? theme.colors.positive + '20' : theme.colors.negative + '20'}
              stroke={curr.label === 1 ? theme.colors.positive : theme.colors.negative} strokeWidth="2" />
            <text x="375" y="96" textAnchor="middle" fill={theme.colors.textMuted} fontSize="8" fontFamily="JetBrains Mono">
              true
            </text>
            <text x="375" y="110" textAnchor="middle" 
              fill={curr.label === 1 ? theme.colors.positive : theme.colors.negative}
              fontSize="14" fontWeight="bold" fontFamily="JetBrains Mono">
              {curr.label}
            </text>
          </>
        )}

        {/* Flow labels */}
        <text x="50" y="185" textAnchor="middle" fill={theme.colors.textMuted} fontSize="9" fontFamily="JetBrains Mono">
          Inputs
        </text>
        <text x="200" y="185" textAnchor="middle" fill={theme.colors.textMuted} fontSize="9" fontFamily="JetBrains Mono">
          Neuron
        </text>
        <text x="320" y="185" textAnchor="middle" fill={theme.colors.textMuted} fontSize="9" fontFamily="JetBrains Mono">
          Output
        </text>
      </svg>
    );
  };

  // Sigmoid curve canvas
  const SigmoidCanvas = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
      const canvas = canvasRef.current;
      if (!canvas) return;
      const ctx = canvas.getContext('2d');
      const w = canvas.width, h = canvas.height;

      ctx.fillStyle = theme.colors.surfaceAlt;
      ctx.fillRect(0, 0, w, h);

      const pad = 25;
      const scaleX = (z) => pad + ((z + 4) / 8) * (w - 2 * pad);
      const scaleY = (p) => h - pad - p * (h - 2 * pad);

      // Grid
      ctx.strokeStyle = theme.colors.border;
      ctx.lineWidth = 0.5;
      ctx.setLineDash([2, 2]);
      for (let p of [0.5]) {
        ctx.beginPath();
        ctx.moveTo(pad, scaleY(p));
        ctx.lineTo(w - pad, scaleY(p));
        ctx.stroke();
      }
      ctx.setLineDash([]);

      // Axes
      ctx.strokeStyle = theme.colors.textMuted;
      ctx.lineWidth = 1;
      ctx.beginPath();
      ctx.moveTo(pad, scaleY(0));
      ctx.lineTo(w - pad, scaleY(0));
      ctx.stroke();
      ctx.beginPath();
      ctx.moveTo(scaleX(0), pad);
      ctx.lineTo(scaleX(0), h - pad);
      ctx.stroke();

      // Sigmoid curve
      ctx.strokeStyle = theme.colors.primary;
      ctx.lineWidth = 2;
      ctx.beginPath();
      for (let z = -4; z <= 4; z += 0.1) {
        const x = scaleX(z), y = scaleY(sigmoid(z));
        z === -4 ? ctx.moveTo(x, y) : ctx.lineTo(x, y);
      }
      ctx.stroke();

      // Current point
      if (step >= 2) {
        const zClamped = Math.max(-4, Math.min(4, curr.z));
        const x = scaleX(zClamped);
        const y = scaleY(curr.prob);

        // Vertical line
        ctx.strokeStyle = theme.colors.yellow + '60';
        ctx.setLineDash([3, 3]);
        ctx.beginPath();
        ctx.moveTo(x, scaleY(0));
        ctx.lineTo(x, y);
        ctx.stroke();
        ctx.setLineDash([]);

        // Point
        ctx.fillStyle = theme.colors.yellow;
        ctx.beginPath();
        ctx.arc(x, y, 6, 0, Math.PI * 2);
        ctx.fill();
        ctx.strokeStyle = '#fff';
        ctx.lineWidth = 2;
        ctx.stroke();
      }

      // Labels
      ctx.fillStyle = theme.colors.textMuted;
      ctx.font = '9px JetBrains Mono';
      ctx.textAlign = 'center';
      ctx.fillText('z', w - 10, scaleY(0) - 5);
      ctx.fillText('0', scaleX(0) + 8, scaleY(0) + 10);
      ctx.textAlign = 'right';
      ctx.fillText('1', scaleX(0) - 4, scaleY(1) + 4);
      ctx.fillText('.5', scaleX(0) - 4, scaleY(0.5) + 4);

    }, [step, curr]);

    return <canvas ref={canvasRef} width={160} height={100} style={{ borderRadius: '6px' }} />;
  };

  return (
    <div style={{ fontFamily: theme.font, background: theme.colors.background, color: theme.colors.text, minHeight: '100vh', padding: '12px', boxSizing: 'border-box' }}>

      {/* Header + Controls */}
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '8px' }}>
        <div>
          <div style={{ fontSize: '15px', fontWeight: '700' }}>Single Neuron with Sigmoid</div>
          <div style={{ fontSize: '10px', color: theme.colors.textSecondary }}>Forward pass visualization</div>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
          <button onClick={() => setStep(Math.max(0, step - 1))} disabled={step === 0}
            style={{ padding: '5px 10px', background: theme.colors.surface, border: 'none', borderRadius: '6px', color: step === 0 ? theme.colors.textMuted : theme.colors.text, cursor: step === 0 ? 'default' : 'pointer', fontSize: '12px' }}>
            {'<'}
          </button>
          <div style={{ display: 'flex', gap: '4px' }}>
            {Array.from({ length: numSteps }, (_, i) => (
              <div key={i} onClick={() => setStep(i)} style={{ width: i === step ? '16px' : '8px', height: '8px', borderRadius: '4px', background: i <= step ? stepInfo[i].color : theme.colors.border, cursor: 'pointer', transition: 'all 0.2s' }} />
            ))}
          </div>
          <button onClick={() => setStep(Math.min(numSteps - 1, step + 1))} disabled={step === numSteps - 1}
            style={{ padding: '5px 10px', background: step === numSteps - 1 ? theme.colors.surface : theme.colors.primary, border: 'none', borderRadius: '6px', color: theme.colors.text, cursor: step === numSteps - 1 ? 'default' : 'pointer', fontSize: '12px' }}>
            {'>'}
          </button>
          <button onClick={() => { setStep(0); setWeights([0.8, -0.5]); setBias(0.1); setSelectedExample(0); }}
            style={{ padding: '5px 8px', background: theme.colors.surface, border: 'none', borderRadius: '6px', color: theme.colors.textSecondary, cursor: 'pointer', fontSize: '12px' }}>
            ~
          </button>
        </div>
      </div>

      {/* Step Info */}
      <div style={{ padding: '6px 10px', background: theme.colors.surface, borderLeft: `3px solid ${stepInfo[step].color}`, borderRadius: '0 6px 6px 0', marginBottom: '8px', fontSize: '10px' }}>
        <span style={{ fontWeight: '600', color: stepInfo[step].color }}>{stepInfo[step].title}: </span>
        <span style={{ color: theme.colors.textSecondary }}>{stepInfo[step].desc}</span>
      </div>

      {/* Example Selector */}
      <div style={{ display: 'flex', gap: '6px', marginBottom: '8px' }}>
        {examples.map((ex, i) => (
          <button key={i} onClick={() => setSelectedExample(i)}
            style={{ 
              flex: 1,
              padding: '6px 8px', 
              background: selectedExample === i ? theme.colors.primary : theme.colors.surface, 
              border: 'none', 
              borderRadius: '6px', 
              color: selectedExample === i ? '#fff' : theme.colors.textSecondary, 
              cursor: 'pointer', 
              fontSize: '9px',
            }}>
            Ex {i + 1}: x=[{ex.features.join(', ')}] y={ex.label}
          </button>
        ))}
      </div>

      {/* Network Diagram */}
      <div style={{ background: theme.colors.surface, borderRadius: '8px', marginBottom: '8px', overflow: 'hidden', border: `1px solid ${theme.colors.border}` }}>
        <NetworkDiagram />
      </div>

      {/* Controls + Sigmoid side by side */}
      <div style={{ display: 'flex', gap: '8px', marginBottom: '8px' }}>

        {/* Weight/Bias Controls */}
        <div style={{ flex: 1, background: theme.colors.surface, borderRadius: '8px', padding: '10px', border: step === 0 ? `2px solid ${theme.colors.primary}` : `1px solid ${theme.colors.border}` }}>
          <div style={{ fontSize: '9px', color: theme.colors.textMuted, marginBottom: '8px' }}>
            Parameters {step === 0 && <span style={{ color: theme.colors.primary }}>(drag to adjust)</span>}
          </div>

          <div style={{ display: 'flex', flexDirection: 'column', gap: '6px' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <span style={{ width: '24px', fontSize: '10px', color: theme.colors.blue }}>w1</span>
              <input type="range" min="-2" max="2" step="0.1" value={weights[0]}
                onChange={(e) => setWeights([parseFloat(e.target.value), weights[1]])}
                disabled={step > 0}
                style={{ flex: 1, accentColor: theme.colors.blue, opacity: step > 0 ? 0.4 : 1 }} />
              <span style={{ width: '36px', fontSize: '11px', color: theme.colors.blue, textAlign: 'right', fontWeight: '600' }}>{weights[0].toFixed(1)}</span>
            </div>

            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <span style={{ width: '24px', fontSize: '10px', color: theme.colors.pink }}>w2</span>
              <input type="range" min="-2" max="2" step="0.1" value={weights[1]}
                onChange={(e) => setWeights([weights[0], parseFloat(e.target.value)])}
                disabled={step > 0}
                style={{ flex: 1, accentColor: theme.colors.pink, opacity: step > 0 ? 0.4 : 1 }} />
              <span style={{ width: '36px', fontSize: '11px', color: theme.colors.pink, textAlign: 'right', fontWeight: '600' }}>{weights[1].toFixed(1)}</span>
            </div>

            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <span style={{ width: '24px', fontSize: '10px', color: theme.colors.orange }}>b</span>
              <input type="range" min="-2" max="2" step="0.1" value={bias}
                onChange={(e) => setBias(parseFloat(e.target.value))}
                disabled={step > 0}
                style={{ flex: 1, accentColor: theme.colors.orange, opacity: step > 0 ? 0.4 : 1 }} />
              <span style={{ width: '36px', fontSize: '11px', color: theme.colors.orange, textAlign: 'right', fontWeight: '600' }}>{bias.toFixed(1)}</span>
            </div>
          </div>
        </div>

        {/* Sigmoid Curve */}
        <div style={{ background: theme.colors.surface, borderRadius: '8px', padding: '10px', border: `1px solid ${theme.colors.border}` }}>
          <div style={{ fontSize: '9px', color: theme.colors.textMuted, marginBottom: '6px' }}>Sigmoid curve</div>
          <SigmoidCanvas />
        </div>
      </div>

      {/* Calculation Steps */}
      {step >= 1 && (
        <div style={{ display: 'flex', gap: '8px', marginBottom: '8px' }}>
          {/* Weighted Sum */}
          <div style={{ flex: 1, background: theme.colors.surface, borderRadius: '8px', padding: '10px', borderLeft: `3px solid ${theme.colors.blue}` }}>
            <div style={{ fontSize: '9px', color: theme.colors.blue, marginBottom: '4px', fontWeight: '600' }}>Weighted Sum</div>
            <div style={{ fontSize: '9px', color: theme.colors.textSecondary, marginBottom: '4px' }}>
              z = ({weights[0].toFixed(1)} * {curr.features[0].toFixed(1)}) + ({weights[1].toFixed(1)} * {curr.features[1].toFixed(1)}) + {bias.toFixed(1)}
            </div>
            <div style={{ fontSize: '14px', color: theme.colors.blue, fontWeight: '700' }}>z = {curr.z.toFixed(4)}</div>
          </div>

          {/* Activation */}
          {step >= 2 && (
            <div style={{ flex: 1, background: theme.colors.surface, borderRadius: '8px', padding: '10px', borderLeft: `3px solid ${theme.colors.yellow}` }}>
              <div style={{ fontSize: '9px', color: theme.colors.yellow, marginBottom: '4px', fontWeight: '600' }}>Sigmoid Activation</div>
              <div style={{ fontSize: '9px', color: theme.colors.textSecondary, marginBottom: '4px' }}>
                sig(z) = 1 / (1 + e^(-{curr.z.toFixed(2)}))
              </div>
              <div style={{ fontSize: '14px', color: theme.colors.yellow, fontWeight: '700' }}>prob = {curr.prob.toFixed(4)}</div>
            </div>
          )}
        </div>
      )}

      {/* Loss Calculation */}
      {step >= 3 && (
        <div style={{ background: theme.colors.surface, borderRadius: '8px', padding: '10px', marginBottom: '8px', borderLeft: `3px solid ${theme.colors.green}` }}>
          <div style={{ fontSize: '9px', color: theme.colors.green, marginBottom: '8px', fontWeight: '600' }}>Mean Squared Error</div>

          <div style={{ display: 'flex', gap: '8px', marginBottom: '8px' }}>
            {results.map((r, i) => (
              <div key={i} onClick={() => setSelectedExample(i)} style={{ 
                flex: 1, 
                padding: '8px', 
                background: selectedExample === i ? theme.colors.primary + '20' : theme.colors.surfaceAlt, 
                borderRadius: '6px', 
                textAlign: 'center',
                cursor: 'pointer',
                border: selectedExample === i ? `1px solid ${theme.colors.primary}` : `1px solid ${theme.colors.border}`,
              }}>
                <div style={{ fontSize: '8px', color: theme.colors.textMuted }}>Example {i + 1}</div>
                <div style={{ fontSize: '10px', color: theme.colors.textSecondary }}>
                  ({r.prob.toFixed(2)} - {r.label})^2
                </div>
                <div style={{ fontSize: '12px', color: theme.colors.pink, fontWeight: '600' }}>{r.error.toFixed(4)}</div>
              </div>
            ))}
          </div>

          <div style={{ padding: '8px', background: theme.colors.green + '15', borderRadius: '6px', textAlign: 'center', border: `1px solid ${theme.colors.green}` }}>
            <div style={{ fontSize: '9px', color: theme.colors.textSecondary }}>
              MSE = ({results.map(r => r.error.toFixed(4)).join(' + ')}) / 3
            </div>
            <div style={{ fontSize: '16px', color: theme.colors.green, fontWeight: '700' }}>MSE = {mse.toFixed(4)}</div>
          </div>
        </div>
      )}

      {/* Key Insight */}
      <div style={{ padding: '8px 12px', background: theme.colors.surface, borderRadius: '6px', fontSize: '10px', color: theme.colors.textSecondary }}>
        <span style={{ color: theme.colors.primary, fontWeight: '600' }}>Insight: </span>
        {step === 0 && 'Positive weights amplify inputs, negative weights suppress them. The bias shifts the activation threshold.'}
        {step === 1 && 'The weighted sum z represents total evidence. Large positive z leads to high probability, large negative z to low probability.'}
        {step === 2 && 'Sigmoid squashes any real number into [0,1]. The yellow dot shows where this example lands on the curve.'}
        {step === 3 && 'Lower MSE = better predictions. Try adjusting weights in Step 0 to minimize the error!'}
      </div>
    </div>
  );
}
