import React, { useState } from 'react';

// ============================================
// Design Tokens
// ============================================
const theme = {
  colors: {
    primary: '#6366f1',
    primaryHover: '#4f46e5',
    secondary: '#ffffff',
    background: '#000000',
    surface: '#0a0a0a',
    surfaceAlt: '#111111',
    border: '#1f1f1f',
    borderLight: '#2a2a2a',
    text: '#e5e5e5',
    textSecondary: '#a3a3a3',
    textMuted: '#737373',
    success: '#22c55e',
    error: '#ef4444',
    warning: '#f59e0b',
    blue: '#3b82f6',
    purple: '#a855f7',
    pink: '#ec4899',
    green: '#10b981',
    yellow: '#eab308',
    orange: '#f97316',
    red: '#ef4444',
  },
  font: 'system-ui, -apple-system, sans-serif',
  borderRadius: '8px',
  spacing: {
    xs: '4px',
    sm: '8px',
    md: '16px',
    lg: '24px',
    xl: '32px',
  },
};

// ============================================
// Main Component
// ============================================
export default function RidgeLossVisualizer() {
  // Sample data
  const [X] = useState([[1, 1], [2, 1], [3, 1], [4, 1]]);
  const [yTrue] = useState([2, 3, 4, 5]);
  const [w, setW] = useState([0.2, 2.0]);
  const [alpha, setAlpha] = useState(0.1);

  // Calculate predictions
  const predictions = X.map(row => row[0] * w[0] + row[1] * w[1]);

  // Calculate MSE
  const squaredErrors = yTrue.map((y, i) => Math.pow(y - predictions[i], 2));
  const mse = squaredErrors.reduce((a, b) => a + b, 0) / yTrue.length;

  // Calculate L2 penalty
  const l2Penalty = alpha * w.reduce((sum, weight) => sum + weight * weight, 0);

  // Total Ridge loss
  const ridgeLoss = mse + l2Penalty;

  return (
    <div style={{
      fontFamily: theme.font,
      background: theme.colors.background,
      color: theme.colors.text,
      minHeight: '100vh',
      padding: theme.spacing.lg,
    }}>
      <div style={{
        maxWidth: '900px',
        margin: '0 auto',
      }}>
        {/* Header */}
        <div style={{ marginBottom: theme.spacing.lg }}>
          <h1 style={{
            fontSize: '28px',
            fontWeight: '700',
            marginBottom: theme.spacing.sm,
            background: `linear-gradient(135deg, ${theme.colors.primary}, ${theme.colors.purple})`,
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
          }}>
            Ridge Regression Loss Visualizer
          </h1>
          <p style={{ color: theme.colors.textMuted, fontSize: '14px' }}>
            Explore how Ridge loss combines MSE with L2 regularization
          </p>
        </div>

        {/* Controls */}
        <div style={{
          background: theme.colors.surface,
          border: `1px solid ${theme.colors.border}`,
          borderRadius: theme.borderRadius,
          padding: theme.spacing.md,
          marginBottom: theme.spacing.md,
        }}>
          <h3 style={{ fontSize: '16px', marginBottom: theme.spacing.md, color: theme.colors.text }}>
            Model Parameters
          </h3>

          <div style={{ display: 'flex', flexDirection: 'column', gap: theme.spacing.md }}>
            {/* Weight 0 */}
            <div>
              <label style={{ display: 'block', fontSize: '14px', marginBottom: theme.spacing.xs, color: theme.colors.textSecondary }}>
                Weight w₀: <span style={{ color: theme.colors.primary, fontWeight: '600' }}>{w[0].toFixed(2)}</span>
              </label>
              <input
                type="range"
                min="-2"
                max="2"
                step="0.1"
                value={w[0]}
                onChange={(e) => setW([parseFloat(e.target.value), w[1]])}
                style={{ width: '100%' }}
              />
            </div>

            {/* Weight 1 */}
            <div>
              <label style={{ display: 'block', fontSize: '14px', marginBottom: theme.spacing.xs, color: theme.colors.textSecondary }}>
                Weight w₁: <span style={{ color: theme.colors.primary, fontWeight: '600' }}>{w[1].toFixed(2)}</span>
              </label>
              <input
                type="range"
                min="-2"
                max="5"
                step="0.1"
                value={w[1]}
                onChange={(e) => setW([w[0], parseFloat(e.target.value)])}
                style={{ width: '100%' }}
              />
            </div>

            {/* Alpha */}
            <div>
              <label style={{ display: 'block', fontSize: '14px', marginBottom: theme.spacing.xs, color: theme.colors.textSecondary }}>
                Regularization α: <span style={{ color: theme.colors.orange, fontWeight: '600' }}>{alpha.toFixed(2)}</span>
              </label>
              <input
                type="range"
                min="0"
                max="1"
                step="0.05"
                value={alpha}
                onChange={(e) => setAlpha(parseFloat(e.target.value))}
                style={{ width: '100%' }}
              />
            </div>
          </div>
        </div>

        {/* Loss Breakdown */}
        <div style={{
          background: theme.colors.surface,
          border: `1px solid ${theme.colors.border}`,
          borderRadius: theme.borderRadius,
          padding: theme.spacing.md,
          marginBottom: theme.spacing.md,
        }}>
          <h3 style={{ fontSize: '16px', marginBottom: theme.spacing.md, color: theme.colors.text }}>
            Loss Breakdown
          </h3>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: theme.spacing.md }}>
            {/* MSE */}
            <div style={{
              background: theme.colors.surfaceAlt,
              padding: theme.spacing.md,
              borderRadius: theme.borderRadius,
              border: `1px solid ${theme.colors.borderLight}`,
            }}>
              <div style={{ fontSize: '12px', color: theme.colors.blue, marginBottom: theme.spacing.xs }}>
                Mean Squared Error
              </div>
              <div style={{ fontSize: '24px', fontWeight: '700', color: theme.colors.blue }}>
                {mse.toFixed(4)}
              </div>
            </div>

            {/* L2 Penalty */}
            <div style={{
              background: theme.colors.surfaceAlt,
              padding: theme.spacing.md,
              borderRadius: theme.borderRadius,
              border: `1px solid ${theme.colors.borderLight}`,
            }}>
              <div style={{ fontSize: '12px', color: theme.colors.orange, marginBottom: theme.spacing.xs }}>
                L2 Penalty (α·‖w‖²)
              </div>
              <div style={{ fontSize: '24px', fontWeight: '700', color: theme.colors.orange }}>
                {l2Penalty.toFixed(4)}
              </div>
            </div>

            {/* Total Loss */}
            <div style={{
              background: theme.colors.surfaceAlt,
              padding: theme.spacing.md,
              borderRadius: theme.borderRadius,
              border: `2px solid ${theme.colors.primary}`,
            }}>
              <div style={{ fontSize: '12px', color: theme.colors.primary, marginBottom: theme.spacing.xs }}>
                Ridge Loss
              </div>
              <div style={{ fontSize: '24px', fontWeight: '700', color: theme.colors.primary }}>
                {ridgeLoss.toFixed(4)}
              </div>
            </div>
          </div>
        </div>

        {/* Data Table */}
        <div style={{
          background: theme.colors.surface,
          border: `1px solid ${theme.colors.border}`,
          borderRadius: theme.borderRadius,
          padding: theme.spacing.md,
          marginBottom: theme.spacing.md,
        }}>
          <h3 style={{ fontSize: '16px', marginBottom: theme.spacing.md, color: theme.colors.text }}>
            Predictions vs Actual
          </h3>

          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ borderBottom: `1px solid ${theme.colors.border}` }}>
                <th style={{ padding: theme.spacing.sm, textAlign: 'left', color: theme.colors.textSecondary, fontSize: '12px' }}>Sample</th>
                <th style={{ padding: theme.spacing.sm, textAlign: 'left', color: theme.colors.textSecondary, fontSize: '12px' }}>X</th>
                <th style={{ padding: theme.spacing.sm, textAlign: 'left', color: theme.colors.textSecondary, fontSize: '12px' }}>y_true</th>
                <th style={{ padding: theme.spacing.sm, textAlign: 'left', color: theme.colors.textSecondary, fontSize: '12px' }}>y_pred</th>
                <th style={{ padding: theme.spacing.sm, textAlign: 'left', color: theme.colors.textSecondary, fontSize: '12px' }}>Error²</th>
              </tr>
            </thead>
            <tbody>
              {X.map((row, i) => (
                <tr key={i} style={{ borderBottom: `1px solid ${theme.colors.borderLight}` }}>
                  <td style={{ padding: theme.spacing.sm, color: theme.colors.text }}>{i + 1}</td>
                  <td style={{ padding: theme.spacing.sm, color: theme.colors.text, fontFamily: 'monospace' }}>
                    [{row.join(', ')}]
                  </td>
                  <td style={{ padding: theme.spacing.sm, color: theme.colors.green }}>{yTrue[i]}</td>
                  <td style={{ padding: theme.spacing.sm, color: theme.colors.blue }}>{predictions[i].toFixed(2)}</td>
                  <td style={{ padding: theme.spacing.sm, color: theme.colors.orange }}>{squaredErrors[i].toFixed(4)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Formula Display */}
        <div style={{
          background: theme.colors.surface,
          border: `1px solid ${theme.colors.border}`,
          borderRadius: theme.borderRadius,
          padding: theme.spacing.md,
        }}>
          <h3 style={{ fontSize: '16px', marginBottom: theme.spacing.md, color: theme.colors.text }}>
            Formula
          </h3>
          <div style={{
            fontFamily: 'monospace',
            fontSize: '14px',
            lineHeight: '1.8',
            color: theme.colors.textSecondary,
          }}>
            <div>Ridge Loss = <span style={{ color: theme.colors.blue }}>MSE</span> + <span style={{ color: theme.colors.orange }}>α·‖w‖²</span></div>
            <div style={{ marginTop: theme.spacing.sm }}>
              = <span style={{ color: theme.colors.blue }}>{mse.toFixed(4)}</span> + <span style={{ color: theme.colors.orange }}>{l2Penalty.toFixed(4)}</span>
            </div>
            <div style={{ marginTop: theme.spacing.sm, color: theme.colors.primary, fontWeight: '600' }}>
              = {ridgeLoss.toFixed(4)}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
