};

const sigmoid = (x) => 1 / (1 + Math.exp(-x));

const lstmStep = (x, h_prev, c_prev) => {
  const concat = h_prev * 0.5 + x * 0.5;
  const f = sigmoid(0.5 * concat + 0.1);
  const i = sigmoid(0.5 * concat + 0.1);
  const c_tilde = Math.tanh(0.3 * concat + 0.1);
  const o = sigmoid(0.5 * concat + 0.1);
  const c = f * c_prev + i * c_tilde;
  const h = o * Math.tanh(c);
  return { f, i, c_tilde, o, c, h };
};

const runLSTM = (inputs) => {
  let h = 0, c = 0;
  const steps = [{ t: 0, x: null, h, c, gates: null }];
  inputs.forEach((x, t) => {
    const result = lstmStep(x, h, c);
    h = result.h;
    c = result.c;
    steps.push({ t: t + 1, x, h, c, gates: result });
  });
  return steps;
};

